-- title:  Top Hop
-- author: Mega Sparkmaster
-- desc:   A Tiny Challenge
-- script: lua

level=0
start_y=56
mode="title"

function solid(x,y)
 return solids[mget((x)/8,(y)/8)]
end

function spring(x,y)
 return springs[mget((x)/8,(y)/8)]
end

function init()
 solids={
	 [1]=true,
	 [2]=true,
	 [3]=true,
	 [5]=true,
	 [6]=true,
	 [7]=true,
	 [8]=true,
	 [11]=true,
	 [12]=true,
	 [14]=true,
	 [15]=true,
	 [16]=true,
	 [17]=true,
 	[18]=true
	}
	springs={
 	[3]=true,
 	[7]=true,
 	[8]=true,
 	[11]=true,
	 [14]=true,
 	[18]=true
	}
	map_off=level*30
	p={
	 x=8+map_off*8,
		y=start_y,
		vx=0,
		vy=0,
		s=0.35
 }
	f=0
	mc=0
	sp=3
end

init()
music(1,-1,-1,false)
function TIC()

 cls()

 if mode=="title" then
	 map(30,17)
		print("A Tiny Challenge",10*8,7*8-3,8)
		print("PRESS A/Z TO START",9*8,12*8,12)
		print("Mega Sparkmaster",11*8,16*8,14,false,1,true)
		if btnp(4) then 
		 mode="game" 
			sfx(5)
			music(0)
		end

 elseif mode=="ending" then
  map(0,17)
		print("CONGRATULATIONS!",10*8+4,2*8,4)
		print("".."You have finally".."\n".."reached the exit...",10*8+4,4*8,12)
  print("Thanks for playing!",11*8+4,7*8,4,false,1,true)
		print("Press A/Z to return",11*8+4,8*8,13,false,1,true)
  if btnp(4) then
		 level=0
   start_y=56 
		 init()
			mode="title"
			music(1,-1,-1,false) 
		end
	
	elseif mode=="pause" then
	 map(map_off,0)
		spr(19+f,p.x-map_off*8,p.y,0)
		print("AREA "..level+1,10,10,12,false,1,true)
		print("SPRINGS: "..sp,24*8+2,10,13,false,1,true)
		print("PAUSE",13*8+2,8*8,12)
		if btnp(4) then 
		 mode="game"
			sfx(4) 
		end
	
 elseif mode=="game" then

  p.s=0.35+level/15
 	map_off=level*30
 
  if p.vy==0 then
   p.vx=p.s
 	else
 	 if jumping==true then
	   p.vx=p.s*1.75
	 	else
	 	 p.vx=0
 		end
 	end
	
 	if solid(p.x+p.vx,p.y+p.vy) or solid(p.x+7+p.vx,p.y+p.vy) or solid(p.x+p.vx,p.y+7+p.vy) or solid(p.x+7+p.vx,p.y+7+p.vy) then
 	 p.vx=0
 	end
	
 	if solid(p.x,p.y+8+p.vy) or solid(p.x+7,p.y+8+p.vy) then
   p.vy=0
 		jumping=false
 	else
 	 p.vy=p.vy+0.2
 	end
	
 	if p.vy==0 and mget(p.x/8,p.y/8+1)==2 then 
 		p.vy=-3.5 
 		jumping=true
			sfx(0)
	 end

  if p.vy<0 and (solid(p.x+p.vx,p.y+p.vy) or solid(p.x+7+p.vx,p.y+p.vy)) then
   p.vy=0
 	end
	
  if mget(p.x/8,p.y/8+1)==9 then
 	 init()
		 sync(4)
			sfx(2)
	 end
	
	 if mget(p.x/8,p.y/8)==13 then
		 sync(4)
		 if level==7 then
	   mode="ending"
				music()
				sfx(7)
			else
			 p.x=p.x+16
	 	 start_y=p.y
	   level=level+1
	 	 sp=3
				sfx(3)
			end
 	end
	
	 p.x=p.x+p.vx
	 p.y=p.y+p.vy
	
 	mx,my,mp=mouse()
	
	 if mp then
	  if mc==0 then
		  if spring(mx+map_off*8,my) and sp>0 then
	    mset(mx/8+map_off,my/8,2)
		 		sp=sp-1
					sfx(1)
		 	elseif mget(mx/8+map_off,my/8)==2 and (p.vy>0 or jumping==false) then
		 	 mset(mx/8+map_off,my/8,14)
		 		sp=sp+1
					sfx(1)
		 	end
	 	end
	 	mc=mc+1
	 else
	  mc=0
	 end
	
	 if btnp(4) then 
		 mode="pause"
			sfx(4) 
		end
	
	 if sp>=3 then sp=3 end
 	if mc>=1 then mc=1 end
	
 	map(map_off,0)
 	rectb(math.floor(mx/8)*8,math.floor(my/8)*8,8,8,12)
 	spr(19+f,p.x-map_off*8,p.y,0)
		print("AREA "..level+1,10,10,12,false,1,true)
		print("SPRINGS: "..sp,24*8+2,10,13,false,1,true)

 	f=(f+p.s/5)%4
		
	end
	
end
 







